func:
function getAttrObject(device, propName, attrName)
{
    var props = device[propName];
    var attr = props.find(attr => attr.attr === attrName);
    return attr;
}
var echoSettings = 0;
var ledState = 0;
var ledValue = 0;
var ledPeriod = 0;
var ultraAlarmDistance = 0;

var echoSettingsMask = 0;
var ledStateMask = 0;
var ledValueMask = 0;
var ledPeriodMask = 0;
var ultraAlarmDistanceMask = 0;
if (msg.topic == 'echoSettings')
{
    echoSettings = msg.payload;
    echoSettingsMask = 1;
}
else
{
    var device = msg.payload;
    ledState = getAttrObject(device, 'settings', 'ledState').value;
    ledValue = getAttrObject(device, 'settings', 'ledValue').value;
    ledPeriod = getAttrObject(device, 'settings', 'ledPeriod').value;
    ultraAlarmDistance = getAttrObject(device, 'settings', 'ultraAlarm').value;
    var ultraDist = getAttrObject(device, 'readings', 'ultraDist');
    ultraDist.alarm.limits.low = ultraAlarmDistance;
    ledStateMask = 1;
    ledValueMask = 1;
    ledPeriodMask = 1;
    ultraAlarmDistanceMask = 1;
    for (var ii = 0; ii < msg.payload.settings.length; ++ii)
    {
        if (msg.payload.settings[ii].type == 'scalar')
        {
            msg.payload.settings[ii].alarm.value = 0;
            if (!isNaN(msg.payload.settings[ii].value))
            {
                if (msg.payload.settings[ii].value < msg.payload.settings[ii].alarm.limits.low)  msg.payload.settings[ii].alarm.value = 1;
                if (msg.payload.settings[ii].value < msg.payload.settings[ii].alarm.limits.lolo) msg.payload.settings[ii].alarm.value = 3;
                if (msg.payload.settings[ii].value > msg.payload.settings[ii].alarm.limits.high) msg.payload.settings[ii].alarm.value = 2;
                if (msg.payload.settings[ii].value > msg.payload.settings[ii].alarm.limits.hihi) msg.payload.settings[ii].alarm.value = 4;
            }
        }
    }

    flow.set('device', msg.payload);    
}


var buffer = new ArrayBuffer(40);
var settingsView = new DataView(buffer);
settingsView.setInt32(0,echoSettingsMask, true);
settingsView.setInt32(4,ledStateMask, true);
settingsView.setInt32(8,ledValueMask, true);
settingsView.setInt32(12,ledPeriodMask, true);
settingsView.setInt32(16,ultraAlarmDistanceMask, true);

settingsView.setInt32(20,echoSettings, true);
settingsView.setInt32(24,ledState, true);
settingsView.setInt32(28,ledValue, true);
settingsView.setInt32(32,ledPeriod, true);
settingsView.setInt32(36,ultraAlarmDistance, true);

return {topic:'settings',payload:Buffer.from(buffer)};

var ib;

var photoDetValueView       = new DataView(new ArrayBuffer(4));
var ultraDistanceView       = new DataView(new ArrayBuffer(4));
var ultraAlarmDistanceView  = new DataView(new ArrayBuffer(4));
var tempAvgView             = new DataView(new ArrayBuffer(4));
var ledStateView            = new DataView(new ArrayBuffer(4));
var ledValueView            = new DataView(new ArrayBuffer(4));
var ledPeriodView           = new DataView(new ArrayBuffer(4));

for (ib = 0; ib < 4; ++ib)      photoDetValueView.setUint8(ib, msg.payload[0 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib)      ultraDistanceView.setUint8(ib, msg.payload[1 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib) ultraAlarmDistanceView.setUint8(ib, msg.payload[2 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib)            tempAvgView.setUint8(ib, msg.payload[3 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib)           ledStateView.setUint8(ib, msg.payload[4 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib)           ledValueView.setUint8(ib, msg.payload[5 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib)          ledPeriodView.setUint8(ib, msg.payload[6 * 4 + 3 - ib]);

var device = flow.get('device');
device.readings[0].value = ledStateView.getInt32(0);
device.readings[1].value = photoDetValueView.getInt32(0);
device.readings[2].value = Math.round(100.0 * tempAvgView.getFloat32(0)) / 100.0;
device.readings[3].value = ultraDistanceView.getInt32(0);
device.readings[4].value = flow.get('linkQuality');
device.readings[5].value = flow.get('signalLevel');

return {topic : 'reading', payload : device};

for (var ii = 0; ii < msg.payload.readings.length; ++ii)
{
    if (msg.payload.readings[ii].type == 'scalar')
    {
        msg.payload.readings[ii].alarm.value = 0;
        if (!isNaN(msg.payload.readings[ii].value))
        {
            if (msg.payload.readings[ii].value < msg.payload.readings[ii].alarm.limits.low)  msg.payload.readings[ii].alarm.value = 1;
            if (msg.payload.readings[ii].value < msg.payload.readings[ii].alarm.limits.lolo) msg.payload.readings[ii].alarm.value = 3;
            if (msg.payload.readings[ii].value > msg.payload.readings[ii].alarm.limits.high) msg.payload.readings[ii].alarm.value = 2;
            if (msg.payload.readings[ii].value > msg.payload.readings[ii].alarm.limits.hihi) msg.payload.readings[ii].alarm.value = 4;
        }
    }
}
flow.set('device', msg.payload);
return msg;

var x = [];
var y = [];
var npts = 51;
var harm = msg.payload.settings[4].value;
var deltaPhi = msg.payload.settings[5].value;
var phi = context.get('phi');
if (phi == undefined) phi = -deltaPhi;
phi = phi + deltaPhi;
context.set('phi', phi);
for (var ii = 0; ii < npts; ++ii)
{
   x[ii] = ii * 360.0 / (npts - 1);
   y[ii] = Math.round( 1000 * Math.cos((x[ii] * harm + phi)  * Math.PI / 180.0)) / 1000;
}
msg.payload.readings[6].value[0] = x;
msg.payload.readings[6].value[1] = y;
return msg;

var info = msg.payload.split('\n')[6];
var linkStartIndex = info.indexOf('Link Quality=');
var linkStopIndex = info.indexOf('/70');
var linkQuality = Number(info.substring(linkStartIndex + 13, linkStopIndex));
var levelStartIndex = info.indexOf('level=') + 6;
var levelStopIndex = info.indexOf('dBm') - 1;
var signalLevel = Number(info.substring(levelStartIndex, levelStopIndex));
var val = {linkQuality : linkQuality, signalLevel: signalLevel};
flow.set('linkQuality',linkQuality);
flow.set('signalLevel', signalLevel);
return {topic :'test',payload : val};
